mapper.select:
SELECT e.EID,MAIL_ADDRESS,e.NAME,NAME_CN,NAME_KANJI,NAME_KANA,ENGLISH_NAME,BIRTHDAY, GENDER,e.TEL_NO,MOBILE_NO,EMERGENCY_TEL_NO, LOCATION_CODE,SITE_CODE,s.name siteName, START_DATE,END_DATE,WORK_STATUS,w.name workname,DEPARTMENT_ID,d.name departName, e.MANAGER_EID,e.TYPE,t.name typeName,COMPANY_ID,c.name companyName, YEARS_AT_JAPAN,PASSPORT_FLAG, PASSPORT_EXPIRE_DATE,VISA_FLAG,VISA_TYPE,VISA_EXPIRE_DATE, JAPANESE_LEVEL,REENTRY_LIMITED_DATE,BILLABLE_FLAG, MANAGER_FLAG,r.ROLE_CODE,rcode.name rolename, e.INS_USER_ID,e.INS_TIMESTAMP,e.UPD_USER_ID,e.UPD_TIMESTAMP FROM employee_info e INNER JOIN (SELECT CATEGORY_CODE, CODE,NAME FROM cd_master_m WHERE CATEGORY_CODE = '0010') s ON s.CODE = e.SITE_CODE INNER JOIN (SELECT CATEGORY_CODE, CODE,NAME FROM cd_master_m WHERE CATEGORY_CODE = '0050') t ON t.code = e.type INNER JOIN (SELECT CATEGORY_CODE, CODE,NAME FROM cd_master_m WHERE CATEGORY_CODE = '1090') w ON w.code = e.WORK_STATUS LEFT JOIN company_m c ON c.ID = e.COMPANY_ID LEFT JOIN (SELECT ID, NAME, MANAGER_EID,TYPE,PARENT_DEPARTMENT_ID FROM department_m) d ON d.id = e.DEPARTMENT_ID LEFT JOIN (SELECT EID, ROLE_CODE FROM emp_role_info) r ON r.eid = e.EID INNER JOIN (SELECT CATEGORY_CODE, CODE,NAME FROM cd_master_m WHERE CATEGORY_CODE = '0040') rcode ON rcode.code = r.ROLE_CODE INNER JOIN emp_job_info j ON j.EID = e.EID
and e.eid = #{emp.eid}
and e.name like '%${emp.name}%'
and e.SITE_CODE = #{emp.site.code}
and START_DATE >= #{startDateFrom}
and START_DATE <= #{startDateTo}
and JOB_POSITION_CODE = #{postCode}
